import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { 
  Plus, 
  Users, 
  Calendar, 
  Target, 
  TrendingUp,
  Edit,
  Trash2,
  UserPlus,
  Clock,
  ArrowLeft,
  Home
} from "lucide-react";
import { Link } from "wouter";
import type { Project, User } from "@shared/schema";

export default function ProjectManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [showCreateProject, setShowCreateProject] = useState(false);
  const [showAssignDialog, setShowAssignDialog] = useState(false);
  const [selectedProject, setSelectedProject] = useState<Project | null>(null);

  // Form states
  const [projectTitle, setProjectTitle] = useState("");
  const [projectDescription, setProjectDescription] = useState("");
  const [projectType, setProjectType] = useState("research");
  const [priority, setPriority] = useState("medium");
  const [deadline, setDeadline] = useState("");
  const [estimatedHours, setEstimatedHours] = useState("");
  const [selectedUserId, setSelectedUserId] = useState("");

  // Fetch projects
  const { data: projects, isLoading: projectsLoading } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
  });

  // Fetch users for assignment
  const { data: users, isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Create project mutation
  const createProjectMutation = useMutation({
    mutationFn: async (projectData: any) => {
      return await apiRequest("POST", "/api/projects", projectData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects"] });
      toast({
        title: "Project Created",
        description: "New project has been created successfully",
      });
      setShowCreateProject(false);
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create project",
        variant: "destructive",
      });
    },
  });

  // Assign project mutation
  const assignProjectMutation = useMutation({
    mutationFn: async (assignmentData: any) => {
      return await apiRequest("POST", "/api/assignments", assignmentData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects"] });
      toast({
        title: "Project Assigned",
        description: "Project has been assigned successfully",
      });
      setShowAssignDialog(false);
      setSelectedProject(null);
      setSelectedUserId("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to assign project",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setProjectTitle("");
    setProjectDescription("");
    setProjectType("research");
    setPriority("medium");
    setDeadline("");
    setEstimatedHours("");
  };

  const handleCreateProject = (e: React.FormEvent) => {
    e.preventDefault();
    if (!projectTitle.trim() || !projectDescription.trim()) {
      toast({
        title: "Missing Information",
        description: "Please provide project title and description",
        variant: "destructive",
      });
      return;
    }

    createProjectMutation.mutate({
      name: projectTitle,
      description: projectDescription,
      projectType: projectType,
      targetEndDate: deadline || undefined,
      status: "planning"
    });
  };

  const handleAssignProject = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedProject || !selectedUserId) {
      toast({
        title: "Missing Information",
        description: "Please select both project and user",
        variant: "destructive",
      });
      return;
    }

    assignProjectMutation.mutate({
      projectId: selectedProject.id,
      userId: selectedUserId,
      role: "assignee"
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'planning': return 'bg-yellow-100 text-yellow-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'on_hold': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (projectsLoading) {
    return (
      <div className="min-h-screen bg-background p-6">
        {/* Navigation Breadcrumbs */}
        <div className="flex items-center space-x-2 mb-6">
          <Link href="/">
            <Button variant="ghost" size="sm" className="text-muted-foreground hover:text-foreground" data-testid="nav-home">
              <Home className="h-4 w-4 mr-2" />
              Dashboard
            </Button>
          </Link>
          <span className="text-muted-foreground">â†’</span>
          <span className="text-foreground font-medium">Project Management</span>
        </div>

        <div className="space-y-6">
          <div className="flex justify-between items-center">
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-10 w-32" />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {Array.from({ length: 6 }).map((_, i) => (
              <Skeleton key={i} className="h-64" />
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      {/* Navigation Breadcrumbs */}
      <div className="flex items-center space-x-2 mb-6">
        <Link href="/">
          <Button variant="ghost" size="sm" className="text-muted-foreground hover:text-foreground" data-testid="nav-home">
            <Home className="h-4 w-4 mr-2" />
            Dashboard
          </Button>
        </Link>
        <span className="text-muted-foreground">â†’</span>
        <span className="text-foreground font-medium">Project Management</span>
      </div>

      <div className="space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-foreground">Project Management</h1>
            <p className="text-muted-foreground">Create and manage research projects</p>
          </div>

        <Dialog open={showCreateProject} onOpenChange={setShowCreateProject}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-project">
              <Plus className="mr-2 h-4 w-4" />
              Create Project
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Project</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleCreateProject} className="space-y-4">
              <div>
                <Label htmlFor="project-title">Project Title</Label>
                <Input
                  id="project-title"
                  value={projectTitle}
                  onChange={(e) => setProjectTitle(e.target.value)}
                  placeholder="Machine Learning for Health Data Analysis"
                  className="mt-2"
                  data-testid="input-project-title"
                  required
                />
              </div>

              <div>
                <Label htmlFor="project-description">Description</Label>
                <Textarea
                  id="project-description"
                  value={projectDescription}
                  onChange={(e) => setProjectDescription(e.target.value)}
                  placeholder="Detailed description of the research project..."
                  rows={4}
                  className="mt-2"
                  data-testid="textarea-project-description"
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="project-type">Project Type</Label>
                  <Select value={projectType} onValueChange={setProjectType}>
                    <SelectTrigger className="mt-2" data-testid="select-project-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="research">Research</SelectItem>
                      <SelectItem value="development">Development</SelectItem>
                      <SelectItem value="analysis">Data Analysis</SelectItem>
                      <SelectItem value="publication">Publication</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="priority">Priority</Label>
                  <Select value={priority} onValueChange={setPriority}>
                    <SelectTrigger className="mt-2" data-testid="select-priority">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="high">High</SelectItem>
                      <SelectItem value="medium">Medium</SelectItem>
                      <SelectItem value="low">Low</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="deadline">Deadline (Optional)</Label>
                  <Input
                    id="deadline"
                    type="date"
                    value={deadline}
                    onChange={(e) => setDeadline(e.target.value)}
                    className="mt-2"
                    data-testid="input-deadline"
                  />
                </div>

                <div>
                  <Label htmlFor="estimated-hours">Estimated Hours (Optional)</Label>
                  <Input
                    id="estimated-hours"
                    type="number"
                    value={estimatedHours}
                    onChange={(e) => setEstimatedHours(e.target.value)}
                    placeholder="120"
                    className="mt-2"
                    data-testid="input-estimated-hours"
                  />
                </div>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setShowCreateProject(false)}
                  data-testid="button-cancel-create"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createProjectMutation.isPending}
                  data-testid="button-submit-create"
                >
                  {createProjectMutation.isPending ? "Creating..." : "Create Project"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Project Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Target className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Total Projects</p>
                <p className="text-2xl font-bold">{projects?.length || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <TrendingUp className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Active Projects</p>
                <p className="text-2xl font-bold">
                  {projects?.filter(p => p.status === 'in_progress').length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Users className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Team Members</p>
                <p className="text-2xl font-bold">{users?.length || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Clock className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Completed</p>
                <p className="text-2xl font-bold">
                  {projects?.filter(p => p.status === 'completed').length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Projects Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Projects</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Project</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Priority</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Progress</TableHead>
                <TableHead>Deadline</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {projects?.map((project) => (
                <TableRow key={project.id}>
                  <TableCell>
                    <div>
                      <p className="font-medium">{project.name}</p>
                      <p className="text-sm text-muted-foreground line-clamp-1">
                        {project.description}
                      </p>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline" className="capitalize">
                      {project.projectType || 'research'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <Badge className={`capitalize ${getPriorityColor('medium')}`}>
                      medium
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <Badge className={`capitalize ${getStatusColor(project.status || 'planning')}`}>
                      {project.status?.replace('_', ' ') || 'planning'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="space-y-1">
                      <Progress value={0} className="w-16" />
                      <p className="text-xs text-muted-foreground">0%</p>
                    </div>
                  </TableCell>
                  <TableCell>
                    {project.targetEndDate ? new Date(project.targetEndDate).toLocaleDateString() : 'No deadline'}
                  </TableCell>
                  <TableCell>
                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => {
                          setSelectedProject(project);
                          setShowAssignDialog(true);
                        }}
                        data-testid={`button-assign-${project.id}`}
                      >
                        <UserPlus className="h-4 w-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        data-testid={`button-edit-${project.id}`}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
              {(!projects || projects.length === 0) && (
                <TableRow>
                  <TableCell colSpan={7} className="text-center py-8">
                    <div className="text-muted-foreground">
                      <Target className="h-12 w-12 mx-auto mb-4 opacity-50" />
                      <p>No projects yet. Create your first project to get started!</p>
                    </div>
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Assignment Dialog */}
      <Dialog open={showAssignDialog} onOpenChange={setShowAssignDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Assign Project</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleAssignProject} className="space-y-4">
            <div>
              <Label>Project</Label>
              <Input
                value={selectedProject?.name || ""}
                disabled
                className="mt-2"
              />
            </div>

            <div>
              <Label htmlFor="assign-user">Assign to Team Member</Label>
              <Select value={selectedUserId} onValueChange={setSelectedUserId}>
                <SelectTrigger className="mt-2" data-testid="select-assign-user">
                  <SelectValue placeholder="Select team member" />
                </SelectTrigger>
                <SelectContent>
                  {users?.filter(u => u.role !== 'admin' && u.role !== 'professor').map((user) => (
                    <SelectItem key={user.id} value={user.id}>
                      {user.firstName} {user.lastName} ({user.role})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="flex justify-end space-x-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setShowAssignDialog(false)}
                data-testid="button-cancel-assign"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={assignProjectMutation.isPending}
                data-testid="button-submit-assign"
              >
                {assignProjectMutation.isPending ? "Assigning..." : "Assign Project"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
      </div>
    </div>
  );
}

client/src/pages/admin/ReportsManagement.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { 
  FileText,
  Download,
  TrendingUp,
  Users,
  Calendar,
  BarChart3,
  PieChart,
  Clock,
  Target,
  AlertTriangle,
  CheckCircle,
  Home,
  Filter,
  RefreshCw,
  FileSpreadsheet,
  Share2
} from "lucide-react";
import { Link } from "wouter";
import type { User, Project } from "@shared/schema";

interface ReportData {
  id: string;
  title: string;
  type: string;
  generatedAt: string;
  createdBy: string;
  status: string;
  fileSize?: string;
  downloadUrl?: string;
}

interface ProductivityReport {
  userId: string;
  userName: string;
  totalHours: number;
  completedTasks: number;
  activeProjects: number;
  productivityScore: number;
  weeklyTrend: number[];
}

interface ProjectReport {
  projectId: string;
  projectName: string;
  status: string;
  progress: number;
  totalHours: number;
  teamSize: number;
  deadline: string;
  riskLevel: 'low' | 'medium' | 'high';
}

export default function ReportsManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [showCreateReport, setShowCreateReport] = useState(false);
  const [selectedReportType, setSelectedReportType] = useState("productivity");
  const [dateRange, setDateRange] = useState("last30days");
  const [filterStatus, setFilterStatus] = useState("all");

  // Form states for report generation
  const [reportTitle, setReportTitle] = useState("");
  const [reportFormat, setReportFormat] = useState("pdf");
  const [includeCharts, setIncludeCharts] = useState(true);

  // Fetch existing reports
  const { data: reports, isLoading: reportsLoading } = useQuery<ReportData[]>({
    queryKey: ["/api/reports"],
    retry: false,
  });

  // Fetch productivity reports
  const { data: productivityData, isLoading: productivityLoading } = useQuery<ProductivityReport[]>({
    queryKey: ["/api/reports/productivity", dateRange],
    retry: false,
  });

  // Fetch project reports
  const { data: projectData, isLoading: projectLoading } = useQuery<ProjectReport[]>({
    queryKey: ["/api/reports/projects", dateRange],
    retry: false,
  });

  // Fetch users for report filtering
  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
    retry: false,
  });

  // Generate report mutation
  const generateReportMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/reports/generate", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Report generation started. You'll be notified when ready.",
      });
      setShowCreateReport(false);
      resetForm();
      queryClient.invalidateQueries({ queryKey: ["/api/reports"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to generate report",
        variant: "destructive",
      });
    },
  });

  // Download report mutation
  const downloadReportMutation = useMutation({
    mutationFn: async (reportId: string) => {
      return await apiRequest("GET", `/api/reports/${reportId}/download`, {});
    },
    onSuccess: (data: any, reportId) => {
      // Create download link
      const link = document.createElement('a');
      link.href = data.downloadUrl || '#';
      link.download = `report-${reportId}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      toast({
        title: "Success",
        description: "Report downloaded successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to download report",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setReportTitle("");
    setSelectedReportType("productivity");
    setReportFormat("pdf");
    setIncludeCharts(true);
  };

  const handleGenerateReport = (e: React.FormEvent) => {
    e.preventDefault();

    if (!reportTitle.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter a report title",
        variant: "destructive",
      });
      return;
    }

    generateReportMutation.mutate({
      title: reportTitle,
      type: selectedReportType,
      format: reportFormat,
      dateRange,
      includeCharts,
      createdBy: user?.id,
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'completed':
        return <Badge className="bg-green-100 text-green-800"><CheckCircle className="h-3 w-3 mr-1" />Ready</Badge>;
      case 'generating':
        return <Badge className="bg-blue-100 text-blue-800"><RefreshCw className="h-3 w-3 mr-1" />Generating</Badge>;
      case 'failed':
        return <Badge className="bg-red-100 text-red-800"><AlertTriangle className="h-3 w-3 mr-1" />Failed</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800">Unknown</Badge>;
    }
  };

  const getRiskBadge = (riskLevel: string) => {
    switch (riskLevel) {
      case 'high':
        return <Badge className="bg-red-100 text-red-800">High Risk</Badge>;
      case 'medium':
        return <Badge className="bg-yellow-100 text-yellow-800">Medium Risk</Badge>;
      case 'low':
        return <Badge className="bg-green-100 text-green-800">Low Risk</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800">Unknown</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="min-h-screen bg-background p-6">
      {/* Navigation Breadcrumbs */}
      <div className="flex items-center space-x-2 mb-6">
        <Link href="/">
          <Button variant="ghost" size="sm" className="text-muted-foreground hover:text-foreground" data-testid="nav-home">
            <Home className="h-4 w-4 mr-2" />
            Dashboard
          </Button>
        </Link>
        <span className="text-muted-foreground">â†’</span>
        <span className="text-foreground font-medium">Reports & Analytics</span>
      </div>

      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Reports & Analytics</h1>
          <p className="text-muted-foreground">
            Generate comprehensive reports and export analytics data
          </p>
        </div>

        <div className="flex space-x-3">
          <Button variant="outline" data-testid="button-refresh-data">
            <RefreshCw className="mr-2 h-4 w-4" />
            Refresh Data
          </Button>

          <Dialog open={showCreateReport} onOpenChange={setShowCreateReport}>
            <DialogTrigger asChild>
              <Button data-testid="button-generate-report">
                <FileText className="mr-2 h-4 w-4" />
                Generate Report
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Generate New Report</DialogTitle>
              </DialogHeader>
              <form onSubmit={handleGenerateReport} className="space-y-4">
                <div>
                  <Label htmlFor="report-title">Report Title</Label>
                  <Input
                    id="report-title"
                    value={reportTitle}
                    onChange={(e) => setReportTitle(e.target.value)}
                    placeholder="Monthly Productivity Report"
                    className="mt-2"
                    data-testid="input-report-title"
                  />
                </div>

                <div>
                  <Label htmlFor="report-type">Report Type</Label>
                  <Select value={selectedReportType} onValueChange={setSelectedReportType}>
                    <SelectTrigger className="mt-2" data-testid="select-report-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="productivity">Team Productivity</SelectItem>
                      <SelectItem value="projects">Project Status</SelectItem>
                      <SelectItem value="time">Time Analytics</SelectItem>
                      <SelectItem value="performance">Performance Review</SelectItem>
                      <SelectItem value="compliance">Compliance Report</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="date-range">Date Range</Label>
                  <Select value={dateRange} onValueChange={setDateRange}>
                    <SelectTrigger className="mt-2" data-testid="select-date-range">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="last7days">Last 7 Days</SelectItem>
                      <SelectItem value="last30days">Last 30 Days</SelectItem>
                      <SelectItem value="last90days">Last 90 Days</SelectItem>
                      <SelectItem value="lastYear">Last Year</SelectItem>
                      <SelectItem value="custom">Custom Range</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="report-format">Export Format</Label>
                  <Select value={reportFormat} onValueChange={setReportFormat}>
                    <SelectTrigger className="mt-2" data-testid="select-report-format">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="pdf">PDF Report</SelectItem>
                      <SelectItem value="excel">Excel Spreadsheet</SelectItem>
                      <SelectItem value="csv">CSV Data</SelectItem>
                      <SelectItem value="json">JSON Export</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex justify-end space-x-2 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setShowCreateReport(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={generateReportMutation.isPending}
                    data-testid="button-submit-report"
                  >
                    {generateReportMutation.isPending ? "Generating..." : "Generate Report"}
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Main Content */}
      <Tabs defaultValue="overview" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="productivity">Productivity</TabsTrigger>
          <TabsTrigger value="projects">Projects</TabsTrigger>
          <TabsTrigger value="reports">My Reports</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-blue-100 rounded-lg">
                    <FileText className="h-6 w-6 text-blue-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Total Reports</p>
                    <p className="text-2xl font-bold">{reports?.length || 0}</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-green-100 rounded-lg">
                    <TrendingUp className="h-6 w-6 text-green-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Avg Productivity</p>
                    <p className="text-2xl font-bold">
                      {productivityData && productivityData.length > 0 
                        ? Math.round(productivityData.reduce((sum, p) => sum + p.productivityScore, 0) / productivityData.length)
                        : 0}%
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-purple-100 rounded-lg">
                    <Users className="h-6 w-6 text-purple-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Active Projects</p>
                    <p className="text-2xl font-bold">
                      {projectData?.filter(p => p.status === 'active').length || 0}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-yellow-100 rounded-lg">
                    <Clock className="h-6 w-6 text-yellow-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Total Hours</p>
                    <p className="text-2xl font-bold">
                      {productivityData?.reduce((sum, p) => sum + p.totalHours, 0) || 0}h
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Quick Actions */}
          <Card>
            <CardHeader>
              <CardTitle>Quick Report Generation</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <Button variant="outline" className="h-24 flex flex-col items-center justify-center space-y-2" data-testid="button-quick-productivity">
                  <TrendingUp className="h-8 w-8 text-blue-600" />
                  <span>Productivity Report</span>
                </Button>
                <Button variant="outline" className="h-24 flex flex-col items-center justify-center space-y-2" data-testid="button-quick-project">
                  <Target className="h-8 w-8 text-green-600" />
                  <span>Project Status Report</span>
                </Button>
                <Button variant="outline" className="h-24 flex flex-col items-center justify-center space-y-2" data-testid="button-quick-time">
                  <Clock className="h-8 w-8 text-purple-600" />
                  <span>Time Analytics Report</span>
                </Button>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Productivity Tab */}
        <TabsContent value="productivity" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Team Productivity Analytics</h2>
            <Button variant="outline" data-testid="button-export-productivity">
              <Download className="mr-2 h-4 w-4" />
              Export Data
            </Button>
          </div>

          <Card>
            <CardContent className="p-6">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Team Member</TableHead>
                    <TableHead>Hours Logged</TableHead>
                    <TableHead>Tasks Completed</TableHead>
                    <TableHead>Active Projects</TableHead>
                    <TableHead>Productivity Score</TableHead>
                    <TableHead>Trend</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {productivityData?.map((member) => (
                    <TableRow key={member.userId}>
                      <TableCell className="font-medium">{member.userName}</TableCell>
                      <TableCell>{member.totalHours}h</TableCell>
                      <TableCell>{member.completedTasks}</TableCell>
                      <TableCell>{member.activeProjects}</TableCell>
                      <TableCell>
                        <div className="flex items-center space-x-2">
                          <span className="font-medium">{member.productivityScore}%</span>
                          {member.productivityScore >= 85 && (
                            <Badge className="bg-green-100 text-green-800">Excellent</Badge>
                          )}
                          {member.productivityScore >= 70 && member.productivityScore < 85 && (
                            <Badge className="bg-blue-100 text-blue-800">Good</Badge>
                          )}
                          {member.productivityScore < 70 && (
                            <Badge className="bg-yellow-100 text-yellow-800">Needs Attention</Badge>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        {member.weeklyTrend?.slice(-4).map((value, index) => (
                          <span key={index} className={`inline-block w-2 h-6 mx-0.5 ${
                            value > 0 ? 'bg-green-400' : value < 0 ? 'bg-red-400' : 'bg-gray-300'
                          }`} />
                        )) || 'No data'}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {!productivityData?.length && (
                <div className="text-center py-8 text-muted-foreground">
                  <TrendingUp className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No productivity data available</p>
                  <p className="text-sm">Data will appear as team members log time</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Projects Tab */}
        <TabsContent value="projects" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Project Status Reports</h2>
            <Button variant="outline" data-testid="button-export-projects">
              <Download className="mr-2 h-4 w-4" />
              Export Data
            </Button>
          </div>

          <Card>
            <CardContent className="p-6">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Project Name</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Progress</TableHead>
                    <TableHead>Team Size</TableHead>
                    <TableHead>Hours Logged</TableHead>
                    <TableHead>Deadline</TableHead>
                    <TableHead>Risk Level</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {projectData?.map((project) => (
                    <TableRow key={project.projectId}>
                      <TableCell className="font-medium">{project.projectName}</TableCell>
                      <TableCell>
                        <Badge className={`${
                          project.status === 'active' ? 'bg-green-100 text-green-800' :
                          project.status === 'planning' ? 'bg-blue-100 text-blue-800' :
                          project.status === 'completed' ? 'bg-gray-100 text-gray-800' :
                          'bg-yellow-100 text-yellow-800'
                        }`}>
                          {project.status}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center space-x-2">
                          <div className="w-24 bg-gray-200 rounded-full h-2">
                            <div 
                              className="bg-blue-600 h-2 rounded-full" 
                              style={{ width: `${project.progress}%` }}
                            />
                          </div>
                          <span className="text-sm font-medium">{project.progress}%</span>
                        </div>
                      </TableCell>
                      <TableCell>{project.teamSize} members</TableCell>
                      <TableCell>{project.totalHours}h</TableCell>
                      <TableCell>{formatDate(project.deadline)}</TableCell>
                      <TableCell>{getRiskBadge(project.riskLevel)}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {!projectData?.length && (
                <div className="text-center py-8 text-muted-foreground">
                  <Target className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No project data available</p>
                  <p className="text-sm">Projects will appear here when created</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* My Reports Tab */}
        <TabsContent value="reports" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Generated Reports</h2>
            <div className="flex space-x-2">
              <Select value={filterStatus} onValueChange={setFilterStatus}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Reports</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="generating">Generating</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <Card>
            <CardContent className="p-6">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Report Title</TableHead>
                    <TableHead>Type</TableHead>
                    <TableHead>Generated</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>File Size</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reports?.map((report) => (
                    <TableRow key={report.id}>
                      <TableCell className="font-medium">{report.title}</TableCell>
                      <TableCell>
                        <Badge variant="outline">{report.type}</Badge>
                      </TableCell>
                      <TableCell>{formatDate(report.generatedAt)}</TableCell>
                      <TableCell>{getStatusBadge(report.status)}</TableCell>
                      <TableCell>{report.fileSize || 'N/A'}</TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          {report.status === 'completed' && (
                            <Button 
                              size="sm" 
                              variant="outline"
                              onClick={() => downloadReportMutation.mutate(report.id)}
                              disabled={downloadReportMutation.isPending}
                              data-testid={`button-download-${report.id}`}
                            >
                              <Download className="h-3 w-3 mr-1" />
                              Download
                            </Button>
                          )}
                          <Button size="sm" variant="outline" data-testid={`button-share-${report.id}`}>
                            <Share2 className="h-3 w-3 mr-1" />
                            Share
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {!reports?.length && (
                <div className="text-center py-8 text-muted-foreground">
                  <FileText className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No reports generated yet</p>
                  <p className="text-sm">Create your first report using the "Generate Report" button</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/admin/ScheduleManagemen.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { 
  Calendar,
  Clock,
  Plus,
  CheckCircle,
  XCircle,
  Edit,
  Trash2,
  Download,
  Upload,
  Users,
  TrendingUp,
  AlertTriangle,
  Home
} from "lucide-react";
import { Link } from "wouter";
import type { User } from "@shared/schema";

interface TimeEntry {
  id: string;
  userId: string;
  projectId?: string;
  date: string;
  startTime: string;
  endTime: string;
  duration: number;
  description: string;
  taskType: string;
  approved: boolean;
  user?: User;
  projectName?: string;
}

interface WorkSchedule {
  id: string;
  userId: string;
  weekStartDate: string;
  totalScheduledHours: number;
  approved: boolean;
  status: string;
  notes?: string;
  user?: User;
}

export default function ScheduleManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [showCreateEntry, setShowCreateEntry] = useState(false);
  const [selectedWeek, setSelectedWeek] = useState(getCurrentWeek());
  const [viewMode, setViewMode] = useState<'team' | 'individual'>('team');

  // Form states
  const [entryDate, setEntryDate] = useState(getTodayDate());
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [description, setDescription] = useState("");
  const [taskType, setTaskType] = useState("research");
  const [selectedUserId, setSelectedUserId] = useState("");

  // Fetch time entries
  const { data: timeEntries, isLoading: entriesLoading } = useQuery<TimeEntry[]>({
    queryKey: ["/api/time-entries", selectedWeek],
    retry: false,
  });

  // Fetch work schedules
  const { data: schedules, isLoading: schedulesLoading } = useQuery<WorkSchedule[]>({
    queryKey: ["/api/work-schedules", selectedWeek],
    retry: false,
  });

  // Fetch users for assignment
  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
    retry: false,
  });

  // Create time entry mutation
  const createTimeEntryMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/time-entries", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Time entry created successfully",
      });
      setShowCreateEntry(false);
      resetForm();
      queryClient.invalidateQueries({ queryKey: ["/api/time-entries"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create time entry",
        variant: "destructive",
      });
    },
  });

  // Approve schedule mutation
  const approveScheduleMutation = useMutation({
    mutationFn: async (scheduleId: string) => {
      return await apiRequest("PUT", `/api/work-schedules/${scheduleId}/approve`, {});
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Schedule approved successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/work-schedules"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve schedule",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setEntryDate(getTodayDate());
    setStartTime("");
    setEndTime("");
    setDescription("");
    setTaskType("research");
    setSelectedUserId("");
  };

  const handleCreateEntry = (e: React.FormEvent) => {
    e.preventDefault();

    if (!startTime || !endTime || !description) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    // Calculate duration
    const duration = calculateDuration(startTime, endTime);
    if (duration <= 0) {
      toast({
        title: "Invalid Time",
        description: "End time must be after start time",
        variant: "destructive",
      });
      return;
    }

    createTimeEntryMutation.mutate({
      userId: user?.id,
      date: entryDate,
      startTime,
      endTime,
      duration,
      description,
      taskType,
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'approved':
        return <Badge className="bg-green-100 text-green-800"><CheckCircle className="h-3 w-3 mr-1" />Approved</Badge>;
      case 'submitted':
        return <Badge className="bg-blue-100 text-blue-800"><Clock className="h-3 w-3 mr-1" />Pending</Badge>;
      case 'rejected':
        return <Badge className="bg-red-100 text-red-800"><XCircle className="h-3 w-3 mr-1" />Rejected</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800">Draft</Badge>;
    }
  };

  const getTaskTypeBadge = (taskType: string) => {
    const colors = {
      research: 'bg-blue-100 text-blue-800',
      development: 'bg-green-100 text-green-800',
      analysis: 'bg-purple-100 text-purple-800',
      meeting: 'bg-orange-100 text-orange-800',
      other: 'bg-gray-100 text-gray-800'
    };
    return <Badge className={colors[taskType as keyof typeof colors] || colors.other}>{taskType}</Badge>;
  };

  return (
    <div className="min-h-screen bg-background p-6">
      {/* Navigation Breadcrumbs */}
      <div className="flex items-center space-x-2 mb-6">
        <Link href="/">
          <Button variant="ghost" size="sm" className="text-muted-foreground hover:text-foreground" data-testid="nav-home">
            <Home className="h-4 w-4 mr-2" />
            Dashboard
          </Button>
        </Link>
        <span className="text-muted-foreground">â†’</span>
        <span className="text-foreground font-medium">Schedule Management</span>
      </div>

      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Schedule Management</h1>
          <p className="text-muted-foreground">
            Manage team schedules, time tracking, and work approvals
          </p>
        </div>

        <div className="flex space-x-3">
          <Button variant="outline" data-testid="button-export-schedules">
            <Download className="mr-2 h-4 w-4" />
            Export Data
          </Button>

          <Dialog open={showCreateEntry} onOpenChange={setShowCreateEntry}>
            <DialogTrigger asChild>
              <Button data-testid="button-log-time">
                <Plus className="mr-2 h-4 w-4" />
                Log Time
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Log Time Entry</DialogTitle>
              </DialogHeader>
              <form onSubmit={handleCreateEntry} className="space-y-4">
                <div>
                  <Label htmlFor="entry-date">Date</Label>
                  <Input
                    id="entry-date"
                    type="date"
                    value={entryDate}
                    onChange={(e) => setEntryDate(e.target.value)}
                    className="mt-2"
                    data-testid="input-entry-date"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="start-time">Start Time</Label>
                    <Input
                      id="start-time"
                      type="time"
                      value={startTime}
                      onChange={(e) => setStartTime(e.target.value)}
                      className="mt-2"
                      data-testid="input-start-time"
                    />
                  </div>
                  <div>
                    <Label htmlFor="end-time">End Time</Label>
                    <Input
                      id="end-time"
                      type="time"
                      value={endTime}
                      onChange={(e) => setEndTime(e.target.value)}
                      className="mt-2"
                      data-testid="input-end-time"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="task-type">Task Type</Label>
                  <Select value={taskType} onValueChange={setTaskType}>
                    <SelectTrigger className="mt-2" data-testid="select-task-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="research">Research</SelectItem>
                      <SelectItem value="development">Development</SelectItem>
                      <SelectItem value="analysis">Analysis</SelectItem>
                      <SelectItem value="meeting">Meeting</SelectItem>
                      <SelectItem value="other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="What did you work on?"
                    className="mt-2"
                    data-testid="textarea-description"
                    rows={3}
                  />
                </div>

                <div className="flex justify-end space-x-2 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setShowCreateEntry(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={createTimeEntryMutation.isPending}
                    data-testid="button-submit-entry"
                  >
                    {createTimeEntryMutation.isPending ? "Saving..." : "Log Time"}
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Main Content */}
      <Tabs defaultValue="time-entries" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="time-entries">Time Entries</TabsTrigger>
          <TabsTrigger value="schedules">Work Schedules</TabsTrigger>
          <TabsTrigger value="analytics">Time Analytics</TabsTrigger>
        </TabsList>

        {/* Time Entries Tab */}
        <TabsContent value="time-entries" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Time Entries</h2>
            <div className="flex items-center space-x-4">
              <Select value={selectedWeek} onValueChange={setSelectedWeek}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value={getCurrentWeek()}>This Week</SelectItem>
                  <SelectItem value={getLastWeek()}>Last Week</SelectItem>
                  <SelectItem value={getNextWeek()}>Next Week</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <Card>
            <CardContent className="p-6">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Date</TableHead>
                    <TableHead>User</TableHead>
                    <TableHead>Time</TableHead>
                    <TableHead>Duration</TableHead>
                    <TableHead>Task Type</TableHead>
                    <TableHead>Description</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {timeEntries?.map((entry) => (
                    <TableRow key={entry.id}>
                      <TableCell>{new Date(entry.date).toLocaleDateString()}</TableCell>
                      <TableCell>
                        {entry.user ? `${entry.user.firstName} ${entry.user.lastName}` : 'Unknown User'}
                      </TableCell>
                      <TableCell>{entry.startTime} - {entry.endTime}</TableCell>
                      <TableCell>{entry.duration}h</TableCell>
                      <TableCell>{getTaskTypeBadge(entry.taskType)}</TableCell>
                      <TableCell className="max-w-48 truncate">{entry.description}</TableCell>
                      <TableCell>
                        {entry.approved ? (
                          <Badge className="bg-green-100 text-green-800">
                            <CheckCircle className="h-3 w-3 mr-1" />
                            Approved
                          </Badge>
                        ) : (
                          <Badge className="bg-yellow-100 text-yellow-800">
                            <Clock className="h-3 w-3 mr-1" />
                            Pending
                          </Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button size="sm" variant="outline">
                            <Edit className="h-3 w-3" />
                          </Button>
                          {!entry.approved && (user?.role === 'admin' || user?.role === 'professor') && (
                            <Button 
                              size="sm" 
                              variant="outline"
                              className="text-green-600 hover:text-green-700"
                            >
                              <CheckCircle className="h-3 w-3" />
                            </Button>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {!timeEntries?.length && (
                <div className="text-center py-8 text-muted-foreground">
                  <Clock className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No time entries found for this period</p>
                  <p className="text-sm">Start by logging your first time entry</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Work Schedules Tab */}
        <TabsContent value="schedules" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Work Schedules</h2>
            <Button variant="outline">
              <Upload className="mr-2 h-4 w-4" />
              Import Schedule
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {schedules?.map((schedule) => (
              <Card key={schedule.id}>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">
                      {schedule.user ? `${schedule.user.firstName} ${schedule.user.lastName}` : 'Unknown User'}
                    </CardTitle>
                    {getStatusBadge(schedule.status)}
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Week of {new Date(schedule.weekStartDate).toLocaleDateString()}
                  </p>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-sm font-medium">Scheduled Hours:</span>
                      <span className="text-sm">{schedule.totalScheduledHours}h</span>
                    </div>

                    {schedule.notes && (
                      <div>
                        <span className="text-sm font-medium">Notes:</span>
                        <p className="text-sm text-muted-foreground mt-1">{schedule.notes}</p>
                      </div>
                    )}

                    {!schedule.approved && (user?.role === 'admin' || user?.role === 'professor') && (
                      <div className="flex space-x-2 pt-2">
                        <Button 
                          size="sm" 
                          className="flex-1"
                          onClick={() => approveScheduleMutation.mutate(schedule.id)}
                          disabled={approveScheduleMutation.isPending}
                        >
                          <CheckCircle className="h-3 w-3 mr-1" />
                          Approve
                        </Button>
                        <Button size="sm" variant="outline" className="flex-1">
                          <XCircle className="h-3 w-3 mr-1" />
                          Reject
                        </Button>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {!schedules?.length && (
            <Card>
              <CardContent className="p-12 text-center">
                <Calendar className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p className="text-lg font-medium mb-2">No schedules found</p>
                <p className="text-muted-foreground">Team schedules will appear here when submitted</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* Time Analytics Tab */}
        <TabsContent value="analytics" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">Time Analytics</h2>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-blue-100 rounded-lg">
                    <Clock className="h-6 w-6 text-blue-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Total Hours</p>
                    <p className="text-2xl font-bold">
                      {timeEntries?.reduce((sum, entry) => sum + entry.duration, 0).toFixed(1) || '0'}h
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-green-100 rounded-lg">
                    <CheckCircle className="h-6 w-6 text-green-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Approved</p>
                    <p className="text-2xl font-bold">
                      {timeEntries?.filter(entry => entry.approved).length || 0}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-yellow-100 rounded-lg">
                    <AlertTriangle className="h-6 w-6 text-yellow-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Pending</p>
                    <p className="text-2xl font-bold">
                      {timeEntries?.filter(entry => !entry.approved).length || 0}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center">
                  <div className="p-2 bg-purple-100 rounded-lg">
                    <Users className="h-6 w-6 text-purple-600" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-muted-foreground">Active Users</p>
                    <p className="text-2xl font-bold">
                      {new Set(timeEntries?.map(entry => entry.userId)).size || 0}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>Time Distribution by Task Type</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {['research', 'development', 'analysis', 'meeting', 'other'].map(taskType => {
                  const typeEntries = timeEntries?.filter(entry => entry.taskType === taskType) || [];
                  const totalHours = typeEntries.reduce((sum, entry) => sum + entry.duration, 0);
                  const percentage = timeEntries?.length ? (totalHours / timeEntries.reduce((sum, entry) => sum + entry.duration, 0)) * 100 : 0;

                  return (
                    <div key={taskType} className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        {getTaskTypeBadge(taskType)}
                        <span className="text-sm capitalize">{taskType}</span>
                      </div>
                      <div className="flex items-center space-x-3">
                        <span className="text-sm font-medium">{totalHours.toFixed(1)}h</span>
                        <span className="text-sm text-muted-foreground">({percentage.toFixed(1)}%)</span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

// Helper functions
function getCurrentWeek(): string {
  const today = new Date();
  const monday = new Date(today.setDate(today.getDate() - today.getDay() + 1));
  return monday.toISOString().split('T')[0];
}

function getLastWeek(): string {
  const today = new Date();
  const lastWeek = new Date(today.setDate(today.getDate() - 7));
  const monday = new Date(lastWeek.setDate(lastWeek.getDate() - lastWeek.getDay() + 1));
  return monday.toISOString().split('T')[0];
}

function getNextWeek(): string {
  const today = new Date();
  const nextWeek = new Date(today.setDate(today.getDate() + 7));
  const monday = new Date(nextWeek.setDate(nextWeek.getDate() - nextWeek.getDay() + 1));
  return monday.toISOString().split('T')[0];
}

function getTodayDate(): string {
  return new Date().toISOString().split('T')[0];
}

function calculateDuration(startTime: string, endTime: string): number {
  const [startHour, startMinute] = startTime.split(':').map(Number);
  const [endHour, endMinute] = endTime.split(':').map(Number);

  const startTotalMinutes = startHour * 60 + startMinute;
  const endTotalMinutes = endHour * 60 + endMinute;

  return (endTotalMinutes - startTotalMinutes) / 60;
}

