import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { 
  Settings,
  Shield,
  Bell,
  Database,
  Users,
  Mail,
  Clock,
  FileText,
  Globe,
  Lock,
  Key,
  Download,
  Upload,
  Trash2,
  Save,
  RefreshCw,
  Home,
  AlertTriangle,
  CheckCircle,
  Info
} from "lucide-react";
import { Link } from "wouter";
import type { User } from "@shared/schema";

interface SystemConfig {
  labName: string;
  labDescription: string;
  timezone: string;
  workingHours: {
    start: string;
    end: string;
  };
  notifications: {
    emailEnabled: boolean;
    slackEnabled: boolean;
    weeklyReports: boolean;
    deadlineAlerts: boolean;
  };
  security: {
    passwordPolicy: {
      minLength: number;
      requireSpecialChars: boolean;
      requireNumbers: boolean;
    };
    sessionTimeout: number;
    mfaRequired: boolean;
  };
  dataRetention: {
    logRetentionDays: number;
    reportRetentionDays: number;
    automaticBackups: boolean;
  };
}

interface UserPermission {
  id: string;
  userId: string;
  userName: string;
  role: string;
  permissions: string[];
  isActive: boolean;
  lastLogin?: string;
}

export default function SystemSettings() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [showUserDialog, setShowUserDialog] = useState(false);
  const [selectedUserId, setSelectedUserId] = useState("");
  const [selectedRole, setSelectedRole] = useState("");

  // Form states
  const [labName, setLabName] = useState("Digital Epidemiology Laboratory");
  const [labDescription, setLabDescription] = useState("Epidemiological research and health data science projects");
  const [timezone, setTimezone] = useState("America/New_York");
  const [workingStart, setWorkingStart] = useState("09:00");
  const [workingEnd, setWorkingEnd] = useState("17:00");
  const [emailNotifications, setEmailNotifications] = useState(true);
  const [weeklyReports, setWeeklyReports] = useState(true);
  const [deadlineAlerts, setDeadlineAlerts] = useState(true);
  const [minPasswordLength, setMinPasswordLength] = useState(8);
  const [requireSpecialChars, setRequireSpecialChars] = useState(true);
  const [sessionTimeout, setSessionTimeout] = useState(480); // 8 hours in minutes
  const [mfaRequired, setMfaRequired] = useState(false);
  const [logRetentionDays, setLogRetentionDays] = useState(90);
  const [automaticBackups, setAutomaticBackups] = useState(true);

  // Fetch system configuration
  const { data: systemConfig, isLoading: configLoading } = useQuery<SystemConfig>({
    queryKey: ["/api/admin/system-config"],
    retry: false,
  });

  // Fetch user permissions
  const { data: userPermissions, isLoading: permissionsLoading } = useQuery<UserPermission[]>({
    queryKey: ["/api/admin/user-permissions"],
    retry: false,
  });

  // Fetch all users for management
  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
    retry: false,
  });

  // Save configuration mutation
  const saveConfigMutation = useMutation({
    mutationFn: async (configData: any) => {
      return await apiRequest("PUT", "/api/admin/system-config", configData);
    },
    onSuccess: () => {
      toast({
        title: "Settings Saved",
        description: "System configuration updated successfully",
      });
      setHasUnsavedChanges(false);
      queryClient.invalidateQueries({ queryKey: ["/api/admin/system-config"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save settings",
        variant: "destructive",
      });
    },
  });

  // Update user role mutation
  const updateUserRoleMutation = useMutation({
    mutationFn: async ({ userId, role }: { userId: string; role: string }) => {
      return await apiRequest("PUT", `/api/admin/users/${userId}/role`, { role });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "User role updated successfully",
      });
      setShowUserDialog(false);
      queryClient.invalidateQueries({ queryKey: ["/api/admin/user-permissions"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update user role",
        variant: "destructive",
      });
    },
  });

  const handleSaveConfig = () => {
    const configData = {
      labName,
      labDescription,
      timezone,
      workingHours: { start: workingStart, end: workingEnd },
      notifications: {
        emailEnabled: emailNotifications,
        weeklyReports,
        deadlineAlerts,
      },
      security: {
        passwordPolicy: {
          minLength: minPasswordLength,
          requireSpecialChars,
        },
        sessionTimeout,
        mfaRequired,
      },
      dataRetention: {
        logRetentionDays,
        automaticBackups,
      },
    };

    saveConfigMutation.mutate(configData);
  };

  const handleFieldChange = () => {
    setHasUnsavedChanges(true);
  };

  const getRoleBadge = (role: string) => {
    switch (role) {
      case 'admin':
        return <Badge className="bg-red-100 text-red-800">Admin</Badge>;
      case 'professor':
        return <Badge className="bg-blue-100 text-blue-800">Professor</Badge>;
      case 'postdoc':
        return <Badge className="bg-purple-100 text-purple-800">Postdoc</Badge>;
      case 'student':
        return <Badge className="bg-green-100 text-green-800">Student</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800">{role}</Badge>;
    }
  };

  const getStatusBadge = (isActive: boolean) => {
    return isActive ? (
      <Badge className="bg-green-100 text-green-800">
        <CheckCircle className="h-3 w-3 mr-1" />
        Active
      </Badge>
    ) : (
      <Badge className="bg-gray-100 text-gray-800">
        <AlertTriangle className="h-3 w-3 mr-1" />
        Inactive
      </Badge>
    );
  };

  return (
    <div className="min-h-screen bg-background p-6">
      {/* Navigation Breadcrumbs */}
      <div className="flex items-center space-x-2 mb-6">
        <Link href="/">
          <Button variant="ghost" size="sm" className="text-muted-foreground hover:text-foreground" data-testid="nav-home">
            <Home className="h-4 w-4 mr-2" />
            Dashboard
          </Button>
        </Link>
        <span className="text-muted-foreground">â†’</span>
        <span className="text-foreground font-medium">System Settings</span>
      </div>

      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-foreground">System Settings</h1>
          <p className="text-muted-foreground">
            Configure lab settings, user permissions, and system preferences
          </p>
        </div>

        <div className="flex space-x-3">
          {hasUnsavedChanges && (
            <div className="flex items-center space-x-2 text-amber-600">
              <AlertTriangle className="h-4 w-4" />
              <span className="text-sm">Unsaved changes</span>
            </div>
          )}

          <Button
            onClick={handleSaveConfig}
            disabled={saveConfigMutation.isPending || !hasUnsavedChanges}
            data-testid="button-save-config"
          >
            <Save className="mr-2 h-4 w-4" />
            {saveConfigMutation.isPending ? "Saving..." : "Save Changes"}
          </Button>
        </div>
      </div>

      {/* Main Content */}
      <Tabs defaultValue="general" className="space-y-6">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="general">General</TabsTrigger>
          <TabsTrigger value="notifications">Notifications</TabsTrigger>
          <TabsTrigger value="security">Security</TabsTrigger>
          <TabsTrigger value="users">User Management</TabsTrigger>
          <TabsTrigger value="data">Data & Backup</TabsTrigger>
        </TabsList>

        {/* General Settings Tab */}
        <TabsContent value="general" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Settings className="mr-2 h-5 w-5" />
                Lab Configuration
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="lab-name">Lab Name</Label>
                    <Input
                      id="lab-name"
                      value={labName}
                      onChange={(e) => { setLabName(e.target.value); handleFieldChange(); }}
                      className="mt-2"
                      data-testid="input-lab-name"
                    />
                  </div>

                  <div>
                    <Label htmlFor="timezone">Timezone</Label>
                    <Select value={timezone} onValueChange={(value) => { setTimezone(value); handleFieldChange(); }}>
                      <SelectTrigger className="mt-2" data-testid="select-timezone">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="America/New_York">Eastern Time</SelectItem>
                        <SelectItem value="America/Chicago">Central Time</SelectItem>
                        <SelectItem value="America/Denver">Mountain Time</SelectItem>
                        <SelectItem value="America/Los_Angeles">Pacific Time</SelectItem>
                        <SelectItem value="UTC">UTC</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="space-y-4">
                  <div>
                    <Label htmlFor="lab-description">Lab Description</Label>
                    <Textarea
                      id="lab-description"
                      value={labDescription}
                      onChange={(e) => { setLabDescription(e.target.value); handleFieldChange(); }}
                      className="mt-2"
                      data-testid="textarea-lab-description"
                      rows={3}
                    />
                  </div>
                </div>
              </div>

              <div className="border-t pt-6">
                <h3 className="text-lg font-medium mb-4">Working Hours</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="working-start">Start Time</Label>
                    <Input
                      id="working-start"
                      type="time"
                      value={workingStart}
                      onChange={(e) => { setWorkingStart(e.target.value); handleFieldChange(); }}
                      className="mt-2"
                      data-testid="input-working-start"
                    />
                  </div>
                  <div>
                    <Label htmlFor="working-end">End Time</Label>
                    <Input
                      id="working-end"
                      type="time"
                      value={workingEnd}
                      onChange={(e) => { setWorkingEnd(e.target.value); handleFieldChange(); }}
                      className="mt-2"
                      data-testid="input-working-end"
                    />
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Notifications Tab */}
        <TabsContent value="notifications" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Bell className="mr-2 h-5 w-5" />
                Notification Preferences
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Email Notifications</Label>
                    <p className="text-sm text-muted-foreground">Send system notifications via email</p>
                  </div>
                  <Switch
                    checked={emailNotifications}
                    onCheckedChange={(checked) => { setEmailNotifications(checked); handleFieldChange(); }}
                    data-testid="switch-email-notifications"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Weekly Reports</Label>
                    <p className="text-sm text-muted-foreground">Automatically generate weekly progress reports</p>
                  </div>
                  <Switch
                    checked={weeklyReports}
                    onCheckedChange={(checked) => { setWeeklyReports(checked); handleFieldChange(); }}
                    data-testid="switch-weekly-reports"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Deadline Alerts</Label>
                    <p className="text-sm text-muted-foreground">Send alerts for approaching project deadlines</p>
                  </div>
                  <Switch
                    checked={deadlineAlerts}
                    onCheckedChange={(checked) => { setDeadlineAlerts(checked); handleFieldChange(); }}
                    data-testid="switch-deadline-alerts"
                  />
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Security Tab */}
        <TabsContent value="security" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Shield className="mr-2 h-5 w-5" />
                Security Settings
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-4">
                <div>
                  <Label htmlFor="min-password-length">Minimum Password Length</Label>
                  <Input
                    id="min-password-length"
                    type="number"
                    min="6"
                    max="20"
                    value={minPasswordLength}
                    onChange={(e) => { setMinPasswordLength(parseInt(e.target.value)); handleFieldChange(); }}
                    className="mt-2 w-32"
                    data-testid="input-min-password-length"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Require Special Characters</Label>
                    <p className="text-sm text-muted-foreground">Passwords must contain special characters</p>
                  </div>
                  <Switch
                    checked={requireSpecialChars}
                    onCheckedChange={(checked) => { setRequireSpecialChars(checked); handleFieldChange(); }}
                    data-testid="switch-require-special-chars"
                  />
                </div>

                <div>
                  <Label htmlFor="session-timeout">Session Timeout (minutes)</Label>
                  <Input
                    id="session-timeout"
                    type="number"
                    min="30"
                    max="1440"
                    value={sessionTimeout}
                    onChange={(e) => { setSessionTimeout(parseInt(e.target.value)); handleFieldChange(); }}
                    className="mt-2 w-32"
                    data-testid="input-session-timeout"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Multi-Factor Authentication</Label>
                    <p className="text-sm text-muted-foreground">Require MFA for all users</p>
                  </div>
                  <Switch
                    checked={mfaRequired}
                    onCheckedChange={(checked) => { setMfaRequired(checked); handleFieldChange(); }}
                    data-testid="switch-mfa-required"
                  />
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* User Management Tab */}
        <TabsContent value="users" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">User Management</h2>
            <Dialog open={showUserDialog} onOpenChange={setShowUserDialog}>
              <DialogTrigger asChild>
                <Button data-testid="button-add-user">
                  <Users className="mr-2 h-4 w-4" />
                  Add User
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Add New User</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label>Select User</Label>
                    <Select value={selectedUserId} onValueChange={setSelectedUserId}>
                      <SelectTrigger className="mt-2">
                        <SelectValue placeholder="Choose a user" />
                      </SelectTrigger>
                      <SelectContent>
                        {users?.map((user) => (
                          <SelectItem key={user.id} value={user.id}>
                            {user.firstName} {user.lastName} ({user.email})
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label>Role</Label>
                    <Select value={selectedRole} onValueChange={setSelectedRole}>
                      <SelectTrigger className="mt-2">
                        <SelectValue placeholder="Choose a role" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="student">Student</SelectItem>
                        <SelectItem value="postdoc">Postdoc</SelectItem>
                        <SelectItem value="professor">Professor</SelectItem>
                        <SelectItem value="admin">Admin</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button variant="outline" onClick={() => setShowUserDialog(false)}>
                      Cancel
                    </Button>
                    <Button
                      onClick={() => updateUserRoleMutation.mutate({ userId: selectedUserId, role: selectedRole })}
                      disabled={!selectedUserId || !selectedRole || updateUserRoleMutation.isPending}
                    >
                      {updateUserRoleMutation.isPending ? "Adding..." : "Add User"}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>

          <Card>
            <CardContent className="p-6">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>User</TableHead>
                    <TableHead>Role</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Last Login</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {users?.map((user) => (
                    <TableRow key={user.id}>
                      <TableCell>
                        <div>
                          <p className="font-medium">{user.firstName} {user.lastName}</p>
                          <p className="text-sm text-muted-foreground">{user.email}</p>
                        </div>
                      </TableCell>
                      <TableCell>{getRoleBadge(user.role)}</TableCell>
                      <TableCell>{getStatusBadge(user.isActive ?? false)}</TableCell>
                      <TableCell>
                        Never
                      </TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button size="sm" variant="outline">
                            Edit
                          </Button>
                          {user.role !== 'admin' && (
                            <Button size="sm" variant="outline" className="text-red-600 hover:text-red-700">
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Data & Backup Tab */}
        <TabsContent value="data" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Database className="mr-2 h-5 w-5" />
                Data Management
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-4">
                <div>
                  <Label htmlFor="log-retention">Log Retention (days)</Label>
                  <Input
                    id="log-retention"
                    type="number"
                    min="7"
                    max="365"
                    value={logRetentionDays}
                    onChange={(e) => { setLogRetentionDays(parseInt(e.target.value)); handleFieldChange(); }}
                    className="mt-2 w-32"
                    data-testid="input-log-retention"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label>Automatic Backups</Label>
                    <p className="text-sm text-muted-foreground">Daily automatic database backups</p>
                  </div>
                  <Switch
                    checked={automaticBackups}
                    onCheckedChange={(checked) => { setAutomaticBackups(checked); handleFieldChange(); }}
                    data-testid="switch-automatic-backups"
                  />
                </div>
              </div>

              <div className="border-t pt-6">
                <h3 className="text-lg font-medium mb-4">Data Operations</h3>
                <div className="flex space-x-4">
                  <Button variant="outline" data-testid="button-export-data">
                    <Download className="mr-2 h-4 w-4" />
                    Export All Data
                  </Button>
                  <Button variant="outline" data-testid="button-backup-now">
                    <Database className="mr-2 h-4 w-4" />
                    Backup Now
                  </Button>
                  <Button variant="outline" data-testid="button-restore-backup">
                    <Upload className="mr-2 h-4 w-4" />
                    Restore Backup
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/postdoc/PostdocDashboar.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import Header from "@/components/layout/Header";
import Sidebar from "@/components/layout/Sidebar";
import MetricCard from "@/components/dashboard/MetricCard";
import ActivityFeed from "@/components/dashboard/ActivityFeed";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Projector, Users, FileText, Trophy, Crown, Lightbulb } from "lucide-react";
import { Progress } from "@/components/ui/progress";

const postdocSidebarItems = [
  { id: 'dashboard', label: 'Dashboard', icon: 'chart-line' },
  { id: 'projects', label: 'My Projects', icon: 'project-diagram' },
  { id: 'mentorship', label: 'Mentorship', icon: 'user-friends' },
  { id: 'team-analytics', label: 'Team Analytics', icon: 'chart-bar' },
  { id: 'schedule', label: 'Schedule', icon: 'calendar-alt' },
  { id: 'lab-overview', label: 'Lab Overview', icon: 'microscope' },
];

export default function PostdocDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [activeSection, setActiveSection] = useState('dashboard');

  const { data: userMetrics, isLoading: metricsLoading, error: metricsError } = useQuery({
    queryKey: ["/api/analytics/user", user?.id],
    retry: false,
    enabled: !!user?.id,
  });

  const { data: userProjects, isLoading: projectsLoading, error: projectsError } = useQuery({
    queryKey: ["/api/projects"],
    retry: false,
  });

  const { data: notifications } = useQuery({
    queryKey: ["/api/notifications/user", user?.id],
    retry: false,
    enabled: !!user?.id,
  });

  // Handle unauthorized errors
  if ((metricsError && isUnauthorizedError(metricsError)) || 
      (projectsError && isUnauthorizedError(projectsError))) {
    toast({
      title: "Unauthorized",
      description: "You are logged out. Logging in again...",
      variant: "destructive",
    });
    setTimeout(() => {
      window.location.href = "/api/login";
    }, 500);
    return null;
  }

  const unreadNotifications = notifications?.filter(n => !n.readAt).length || 0;

  return (
    <div className="min-h-screen bg-background">
      <Header 
        user={user} 
        role="Postdoc"
        roleIcon="user-check"
        notificationCount={unreadNotifications}
      />

      <div className="flex h-[calc(100vh-73px)]">
        <Sidebar 
          items={postdocSidebarItems}
          activeSection={activeSection}
          onSectionChange={setActiveSection}
        />

        <main className="flex-1 overflow-auto" data-testid="main-content-postdoc">
          {activeSection === 'dashboard' && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-foreground mb-2">
                  Welcome back, Dr. {user?.lastName || 'Researcher'}!
                </h2>
                <p className="text-muted-foreground">
                  Your research projects and mentorship overview
                </p>
              </div>

              {/* Enhanced Metrics for Postdoc */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                <MetricCard
                  title="Active Projects"
                  value={userMetrics?.activeProjects?.toString() || "0"}
                  subtitle="leading 0 teams"
                  icon={Projector}
                  iconColor="text-primary"
                  iconBgColor="bg-primary/10"
                  isLoading={metricsLoading}
                  data-testid="metric-active-projects"
                />

                <MetricCard
                  title="Mentoring"
                  value="0"
                  subtitle="graduate students"
                  icon={Users}
                  iconColor="text-green-600"
                  iconBgColor="bg-green-100"
                  data-testid="metric-mentees"
                />

                <MetricCard
                  title="Publications"
                  value="0"
                  subtitle="in review"
                  icon={FileText}
                  iconColor="text-secondary"
                  iconBgColor="bg-secondary/10"
                  data-testid="metric-publications"
                />

                <MetricCard
                  title="Team Performance"
                  value={userMetrics?.productivityScore?.toString() || "0"}
                  subtitle="% average"
                  icon={Trophy}
                  iconColor="text-yellow-600"
                  iconBgColor="bg-yellow-100"
                  isLoading={metricsLoading}
                  data-testid="metric-team-performance"
                />
              </div>

              {/* Mentorship Overview */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
                {/* Mentees Status */}
                <div className="bg-card border border-border rounded-lg p-6">
                  <h3 className="text-lg font-semibold mb-4 flex items-center">
                    <Users className="w-5 h-5 text-green-600 mr-2" />
                    Mentees Progress
                  </h3>
                  <div className="text-center py-8 text-muted-foreground">
                    <Users className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>No mentees assigned yet</p>
                    <p className="text-sm">Students will appear here once assigned</p>
                  </div>
                </div>

                {/* Project Leadership */}
                <div className="bg-card border border-border rounded-lg p-6">
                  <h3 className="text-lg font-semibold mb-4 flex items-center">
                    <Crown className="w-5 h-5 text-yellow-600 mr-2" />
                    Leading Projects
                  </h3>
                  {userProjects && userProjects.length > 0 ? (
                    <div className="space-y-4">
                      {userProjects.map((project: any) => (
                        <div key={project.id} className="border border-border rounded-lg p-4" data-testid={`leading-project-${project.id}`}>
                          <div className="flex items-center justify-between mb-2">
                            <h4 className="text-sm font-semibold">{project.name}</h4>
                            <span className="text-xs bg-primary/10 text-primary px-2 py-1 rounded">Leading</span>
                          </div>
                          <p className="text-xs text-muted-foreground mb-2">
                            Due: {project.targetEndDate ? new Date(project.targetEndDate).toLocaleDateString() : 'TBD'}
                          </p>
                          <Progress value={0} className="h-2 mb-1" />
                          <div className="flex justify-between text-xs text-muted-foreground">
                            <span>Progress: 0%</span>
                            <span>Status: {project.status}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-muted-foreground">
                      <Crown className="w-12 h-12 mx-auto mb-2 opacity-50" />
                      <p>No projects under your leadership</p>
                      <p className="text-sm">Projects will appear here once assigned</p>
                    </div>
                  )}
                </div>
              </div>

              {/* Recent Activities and AI Insights */}
              <div className="bg-card border border-border rounded-lg p-6">
                <h3 className="text-lg font-semibold mb-4">Recent Activities & AI Suggestions</h3>
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <ActivityFeed 
                    title="Team Updates"
                    items={[]}
                    emptyMessage="No recent team activities"
                    data-testid="team-updates-feed"
                  />

                  {/* AI Suggestions */}
                  <div>
                    <h4 className="text-sm font-medium text-muted-foreground mb-3">AI Insights & Suggestions</h4>
                    <div className="text-center py-8 text-muted-foreground">
                      <Lightbulb className="w-12 h-12 mx-auto mb-2 opacity-50" />
                      <p>AI insights coming in Phase 2</p>
                      <p className="text-sm">Personalized recommendations will appear here</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeSection !== 'dashboard' && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-foreground mb-2 capitalize">
                  {activeSection.replace('-', ' ')}
                </h2>
                <p className="text-muted-foreground">
                  {activeSection === 'projects' && 'Manage your research projects and collaborations'}
                  {activeSection === 'mentorship' && 'Guide and support graduate students'}
                  {activeSection === 'team-analytics' && 'Analyze team performance and productivity'}
                  {activeSection === 'schedule' && 'Manage your research schedule'}
                  {activeSection === 'lab-overview' && 'View lab-wide activities and insights'}
                </p>
              </div>
              <div className="text-center py-12 text-muted-foreground">
                <div className="w-16 h-16 mx-auto mb-4 opacity-50 bg-muted rounded-full flex items-center justify-center">
                  <span className="text-2xl">âš™ï¸</span>
                </div>
                <p className="text-lg capitalize">{activeSection.replace('-', ' ')} coming in Phase 2</p>
                <p className="text-sm">Enhanced functionality will be available soon</p>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

client/src/pages/student/MyProjects.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { useAuth } from "@/hooks/useAuth";
import { 
  Target, 
  Clock, 
  Calendar, 
  User, 
  FileText,
  PlayCircle,
  PauseCircle,
  CheckCircle
} from "lucide-react";
import type { Project, ProjectAssignment, ProgressUpdate } from "@shared/schema";

interface ProjectWithAssignment extends Project {
  assignment?: ProjectAssignment;
  latestProgress?: ProgressUpdate;
}

export default function MyProjects() {
  const { user } = useAuth();

  // Fetch user's assigned projects
  const { data: projects, isLoading } = useQuery<ProjectWithAssignment[]>({
    queryKey: ["/api/assignments/user", user?.id],
    enabled: !!user?.id,
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300';
      case 'completed': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'paused': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active': return <PlayCircle className="h-4 w-4" />;
      case 'completed': return <CheckCircle className="h-4 w-4" />;
      case 'paused': return <PauseCircle className="h-4 w-4" />;
      default: return <Target className="h-4 w-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <Skeleton className="h-8 w-64" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 6 }).map((_, i) => (
            <Skeleton key={i} className="h-72" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-foreground">My Projects</h1>
          <p className="text-muted-foreground">
            Track your assigned research projects and progress
          </p>
        </div>
      </div>

      {/* Project Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Target className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Total Projects</p>
                <p className="text-2xl font-bold">{projects?.length || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <PlayCircle className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Active</p>
                <p className="text-2xl font-bold">
                  {projects?.filter(p => p.status === 'active').length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <CheckCircle className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Completed</p>
                <p className="text-2xl font-bold">
                  {projects?.filter(p => p.status === 'completed').length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Clock className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Hours This Week</p>
                <p className="text-2xl font-bold">0</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Projects Grid */}
      {projects && projects.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {projects.map((project) => (
            <Card key={project.id} className="hover:shadow-lg transition-shadow">
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-lg mb-2 line-clamp-2">
                      {project.name}
                    </CardTitle>
                    <div className="flex items-center space-x-2">
                      <Badge className={getStatusColor(project.status)} variant="secondary">
                        <span className="flex items-center space-x-1">
                          {getStatusIcon(project.status)}
                          <span className="capitalize">{project.status}</span>
                        </span>
                      </Badge>
                      {project.projectType && (
                        <Badge variant="outline" className="capitalize">
                          {project.projectType}
                        </Badge>
                      )}
                    </div>
                  </div>
                </div>
              </CardHeader>

              <CardContent className="space-y-4">
                <p className="text-sm text-muted-foreground line-clamp-3">
                  {project.description}
                </p>

                <div className="space-y-3">
                  <div className="flex items-center text-sm text-muted-foreground">
                    <Calendar className="h-4 w-4 mr-2" />
                    <span>
                      {project.targetEndDate 
                        ? `Due: ${new Date(project.targetEndDate).toLocaleDateString()}`
                        : 'No deadline set'
                      }
                    </span>
                  </div>

                  {project.assignment && (
                    <div className="flex items-center text-sm text-muted-foreground">
                      <User className="h-4 w-4 mr-2" />
                      <span className="capitalize">
                        Role: {project.assignment.role || 'Assignee'}
                      </span>
                    </div>
                  )}

                  {/* Progress */}
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Progress</span>
                      <span className="font-medium">
                        {project.latestProgress?.percentComplete || 0}%
                      </span>
                    </div>
                    <Progress value={project.latestProgress?.percentComplete || 0} />
                  </div>

                  {project.latestProgress?.hoursWorked && (
                    <div className="flex items-center text-sm text-muted-foreground">
                      <Clock className="h-4 w-4 mr-2" />
                      <span>{project.latestProgress.hoursWorked} hours logged</span>
                    </div>
                  )}
                </div>

                <div className="flex space-x-2 pt-4">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    className="flex-1"
                    data-testid={`button-view-project-${project.id}`}
                  >
                    <FileText className="h-4 w-4 mr-2" />
                    View Details
                  </Button>
                  <Button 
                    size="sm" 
                    className="flex-1"
                    data-testid={`button-log-progress-${project.id}`}
                  >
                    <PlayCircle className="h-4 w-4 mr-2" />
                    Log Progress
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="p-12 text-center">
            <Target className="h-16 w-16 mx-auto mb-4 text-muted-foreground opacity-50" />
            <h3 className="text-xl font-semibold mb-2">No Projects Yet</h3>
            <p className="text-muted-foreground mb-4">
              You haven't been assigned to any projects yet. 
              Your supervisor will assign projects to you through the lab management system.
            </p>
            <p className="text-sm text-muted-foreground">
              Once assigned, you'll be able to track your progress, log hours, and collaborate with your team here.
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/student/StudentDashboar.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import Header from "@/components/layout/Header";
import Sidebar from "@/components/layout/Sidebar";
import MetricCard from "@/components/dashboard/MetricCard";
import ActivityFeed from "@/components/dashboard/ActivityFeed";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Clock, Projector, Calendar, TrendingUp, Plus, BookOpen, HelpCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";

const studentSidebarItems = [
  { id: 'dashboard', label: 'Dashboard', icon: 'chart-line' },
  { id: 'projects', label: 'My Projects', icon: 'project-diagram' },
  { id: 'schedule', label: 'Schedule', icon: 'calendar-alt' },
  { id: 'progress', label: 'Progress', icon: 'tasks' },
  { id: 'insights', label: 'AI Insights', icon: 'lightbulb' },
  { id: 'lab-stats', label: 'Lab Statistics', icon: 'chart-bar' },
];

export default function StudentDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [activeSection, setActiveSection] = useState('dashboard');

  const { data: userMetrics, isLoading: metricsLoading, error: metricsError } = useQuery({
    queryKey: ["/api/analytics/user", user?.id],
    retry: false,
    enabled: !!user?.id,
  });

  const { data: userProjects, isLoading: projectsLoading, error: projectsError } = useQuery({
    queryKey: ["/api/projects"],
    retry: false,
  });

  const { data: notifications } = useQuery({
    queryKey: ["/api/notifications/user", user?.id],
    retry: false,
    enabled: !!user?.id,
  });

  // Handle unauthorized errors
  if ((metricsError && isUnauthorizedError(metricsError)) || 
      (projectsError && isUnauthorizedError(projectsError))) {
    toast({
      title: "Unauthorized",
      description: "You are logged out. Logging in again...",
      variant: "destructive",
    });
    setTimeout(() => {
      window.location.href = "/api/login";
    }, 500);
    return null;
  }

  const unreadNotifications = notifications?.filter(n => !n.readAt).length || 0;

  return (
    <div className="min-h-screen bg-background">
      <Header 
        user={user} 
        role="PhD Student"
        roleIcon="user-graduate"
        notificationCount={unreadNotifications}
      />

      <div className="flex h-[calc(100vh-73px)]">
        <Sidebar 
          items={studentSidebarItems}
          activeSection={activeSection}
          onSectionChange={setActiveSection}
        />

        <main className="flex-1 overflow-auto" data-testid="main-content-student">
          {activeSection === 'dashboard' && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-foreground mb-2">
                  Welcome back, {user?.firstName || 'Student'}!
                </h2>
                <p className="text-muted-foreground">
                  Here's your research progress and upcoming tasks
                </p>
              </div>

              {/* Personal Metrics */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                <MetricCard
                  title="Hours This Week"
                  value="0"
                  subtitle="of 20 required"
                  icon={Clock}
                  iconColor="text-green-600"
                  iconBgColor="bg-green-100"
                  data-testid="metric-weekly-hours"
                />

                <MetricCard
                  title="Active Projects"
                  value={userMetrics?.activeProjects?.toString() || "0"}
                  subtitle="in progress"
                  icon={Projector}
                  iconColor="text-primary"
                  iconBgColor="bg-primary/10"
                  isLoading={metricsLoading}
                  data-testid="metric-active-projects"
                />

                <MetricCard
                  title="Upcoming Deadlines"
                  value="0"
                  subtitle="next 2 weeks"
                  icon={Calendar}
                  iconColor="text-yellow-600"
                  iconBgColor="bg-yellow-100"
                  data-testid="metric-upcoming-deadlines"
                />

                <MetricCard
                  title="Productivity Score"
                  value={userMetrics?.productivityScore?.toString() || "0"}
                  subtitle="% efficiency"
                  icon={TrendingUp}
                  iconColor="text-secondary"
                  iconBgColor="bg-secondary/10"
                  isLoading={metricsLoading}
                  data-testid="metric-productivity-score"
                />
              </div>

              {/* Today's Schedule & Quick Actions */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
                {/* Today's Schedule */}
                <div className="bg-card border border-border rounded-lg p-6">
                  <h3 className="text-lg font-semibold mb-4 flex items-center">
                    <Calendar className="w-5 h-5 text-primary mr-2" />
                    Today's Schedule
                  </h3>
                  <div className="text-center py-8 text-muted-foreground">
                    <Calendar className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>No scheduled activities for today</p>
                    <p className="text-sm">Submit your weekly schedule to get started</p>
                  </div>
                </div>

                {/* Quick Actions */}
                <div className="bg-card border border-border rounded-lg p-6">
                  <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
                  <div className="grid grid-cols-2 gap-3">
                    <Button 
                      variant="outline"
                      className="p-4 h-auto flex-col items-start space-y-2 bg-primary/10 border-primary/20 hover:bg-primary/20 text-left"
                      data-testid="button-update-progress"
                    >
                      <Plus className="w-5 h-5 text-primary" />
                      <span className="text-sm font-medium text-primary">Update Progress</span>
                    </Button>

                    <Button 
                      variant="outline"
                      className="p-4 h-auto flex-col items-start space-y-2 bg-secondary/10 border-secondary/20 hover:bg-secondary/20 text-left"
                      data-testid="button-submit-schedule"
                    >
                      <Calendar className="w-5 h-5 text-secondary" />
                      <span className="text-sm font-medium text-secondary">Submit Schedule</span>
                    </Button>

                    <Button 
                      variant="outline"
                      className="p-4 h-auto flex-col items-start space-y-2 bg-green-50 border-green-200 hover:bg-green-100 text-left"
                      data-testid="button-log-hours"
                    >
                      <Clock className="w-5 h-5 text-green-600" />
                      <span className="text-sm font-medium text-green-600">Log Hours</span>
                    </Button>

                    <Button 
                      variant="outline"
                      className="p-4 h-auto flex-col items-start space-y-2 bg-yellow-50 border-yellow-200 hover:bg-yellow-100 text-left"
                      data-testid="button-get-help"
                    >
                      <HelpCircle className="w-5 h-5 text-yellow-600" />
                      <span className="text-sm font-medium text-yellow-600">Get Help</span>
                    </Button>
                  </div>
                </div>
              </div>

              {/* Project Progress Overview */}
              <div className="bg-card border border-border rounded-lg p-6">
                <h3 className="text-lg font-semibold mb-4">Project Progress</h3>
                {userProjects && userProjects.length > 0 ? (
                  <div className="space-y-6">
                    {userProjects.map((project: any) => (
                      <div key={project.id} className="border border-border rounded-lg p-4" data-testid={`project-${project.id}`}>
                        <div className="flex items-center justify-between mb-3">
                          <h4 className="text-sm font-semibold text-foreground">{project.name}</h4>
                          <span className="text-xs text-muted-foreground">
                            Due: {project.targetEndDate ? new Date(project.targetEndDate).toLocaleDateString() : 'TBD'}
                          </span>
                        </div>

                        <div className="mb-3">
                          <div className="flex justify-between text-xs text-muted-foreground mb-1">
                            <span>Overall Progress</span>
                            <span>0%</span>
                          </div>
                          <Progress value={0} className="h-2" />
                        </div>

                        <div className="grid grid-cols-3 gap-4 text-xs">
                          <div>
                            <p className="text-muted-foreground mb-1">Literature Review</p>
                            <Progress value={0} className="h-1" />
                          </div>
                          <div>
                            <p className="text-muted-foreground mb-1">Data Analysis</p>
                            <Progress value={0} className="h-1" />
                          </div>
                          <div>
                            <p className="text-muted-foreground mb-1">Writing</p>
                            <Progress value={0} className="h-1" />
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-muted-foreground">
                    <Projector className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>No projects assigned yet</p>
                    <p className="text-sm">Contact your supervisor to get started</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {(activeSection !== 'dashboard' && activeSection !== 'projects') && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-foreground mb-2 capitalize">
                  {activeSection.replace('-', ' ')}
                </h2>
                <p className="text-muted-foreground">
                  {activeSection === 'schedule' && 'Manage your work schedule and check-ins'}
                  {activeSection === 'progress' && 'Track your research progress and milestones'}
                  {activeSection === 'insights' && 'AI-powered insights and recommendations'}
                  {activeSection === 'lab-stats' && 'View lab-wide statistics and benchmarks'}
                </p>
              </div>
              <div className="text-center py-12 text-muted-foreground">
                <div className="w-16 h-16 mx-auto mb-4 opacity-50 bg-muted rounded-full flex items-center justify-center">
                  <span className="text-2xl">ðŸš§</span>
                </div>
                <p className="text-lg capitalize">{activeSection.replace('-', ' ')} coming in Phase 2</p>
                <p className="text-sm">Enhanced functionality will be available soon</p>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

